#include <set>
#include <stdio.h>
#include <string>

int check_distinct(std::string s, long unsigned int n) {
	for(long unsigned int i=0;i<=s.length();i+=1) {
		std::set <int> set;
		for(long unsigned int j=i;j<i+n;j++) {
			set.insert(s[j]);
		}
		if (set.size() == n) {
			return i+n;
		}
	}
	return 0;
}

int main() {
	std::string s= "";
	printf("%d %d\n", check_distinct(s,4), check_distinct(s,14));
}
